-- Deleting old language database and replace it with google language code languages.

USE `oibs`;

alter table contents_cnt modify language_cnt varchar(5) NOT NULL;

delete from languages_lng;
alter table languages_lng modify iso6391_lng varchar(5) NOT NULL;


UPDATE contents_cnt SET language_cnt = 'en' WHERE language_cnt NOT IN (SELECT iso6391_lng from languages_lng);

